import React, { useState } from 'react';
import ReservationForm from './ReservationForm';
import AdminLogin from './AdminLogin';
import AdminDashboard from './AdminDashboard';

function App() {
  const [view, setView] = useState('receptionist');
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsAdminLoggedIn(true);
  };

  return (
    <div className="App">
      <nav className="p-4 bg-blue-500 text-white">
        <button onClick={() => setView('receptionist')} className="mr-4">Receptionist View</button>
        <button onClick={() => setView('admin')} className="mr-4">Admin View</button>
      </nav>
      {view === 'receptionist' && <ReservationForm />}
      {view === 'admin' && (isAdminLoggedIn ? <AdminDashboard /> : <AdminLogin onLogin={handleLogin} />)}
    </div>
  );
}

export default App;
