import React, { useState, useEffect } from 'react';
import './ReservationForm.css';

const ReservationForm = () => {
  const [formData, setFormData] = useState({
    guestName: '',
    address: '',
    mobileNumber: '',
    age: '',
    occupation: '',
    checkInDate: '',
    checkOutDate: '',
    roomId: '',
    price: ''
  });
  const [availableRooms, setAvailableRooms] = useState([]);

  useEffect(() => {
    const fetchAvailableRooms = async () => {
      const response = await fetch('/reservations/available-rooms?checkInDate=' + formData.checkInDate + '&checkOutDate=' + formData.checkOutDate);
      const data = await response.json();
      setAvailableRooms(data);
    };

    if (formData.checkInDate && formData.checkOutDate) {
      fetchAvailableRooms();
    }
  }, [formData.checkInDate, formData.checkOutDate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('/reservations/create', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });

    if (response.ok) {
      alert('Reservation created successfully');
      setFormData({
        guestName: '',
        address: '',
        mobileNumber: '',
        age: '',
        occupation: '',
        checkInDate: '',
        checkOutDate: '',
        roomId: '',
        price: ''
      });
    } else {
      alert('Failed to create reservation');
    }
  };

  return (
    <div className="w-full shadow">
      <h2 className="text-2xl">Reservation Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <label className="block">Name</label>
          <input type="text" name="guestName" value={formData.guestName} onChange={handleChange} required />
        </div>
        <div className="input-container">
          <label className="block">Address</label>
          <input type="text" name="address" value={formData.address} onChange={handleChange} required />
        </div>
        <div className="input-container">
          <label className="block">Mobile Number</label>
          <input type="text" name="mobileNumber" value={formData.mobileNumber} onChange={handleChange} required />
        </div>
        <div className="input-container">
          <label className="block">Age</label>
          <input type="number" name="age" value={formData.age} onChange={handleChange} required />
        </div>
        <div className="input-container">
          <label className="block">Occupation</label>
          <input type="text" name="occupation" value={formData.occupation} onChange={handleChange} required />
        </div>
        <div className="input-container">
          <label className="block">Check-in Date</label>
          <input type="date" name="checkInDate" value={formData.checkInDate} onChange={handleChange} required />
        </div>
        <div className="input-container">
          <label className="block">Check-out Date</label>
          <input type="date" name="checkOutDate" value={formData.checkOutDate} onChange={handleChange} required />
        </div>
        <div className="input-container">
          <label className="block">Room</label>
          <select name="roomId" value={formData.roomId} onChange={handleChange} required>
            <option value="">Select a room</option>
            {availableRooms.map((room) => (
              <option key={room._id} value={room._id}>{room.roomNumber} - {room.roomType}</option>
            ))}
          </select>
        </div>
        <div className="input-container">
          <label className="block">Price</label>
          <input type="number" name="price" value={formData.price} onChange={handleChange} required />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ReservationForm;
